import React from "react";
import { TransformWrapper, TransformComponent } from "react-zoom-pan-pinch";
import "./styles.css";
import mapArray from "./mapArray";
// Marsa Add 1
import Selecto from "react-selecto";

export default function App() {
  const [selectedPixels, setSelected] = React.useState([]);
  console.log("selectedPixels", selectedPixels);
  return (
    <TransformWrapper
      maxScale={40}
      doubleClick={{
        step: 1,
      }}
    >
      <div className="App">
        <Selecto
          // The container to add a selection element
          container={document.body}
          // The area to drag selection element (default: container)
          dragContainer={window}
          // Targets to select. You can register a queryselector or an Element.
          selectableTargets={[".container", ".child"]}
          // Whether to select by click (default: true)
          selectByClick={true}
          // Whether to select from the target inside (default: true)
          selectFromInside={true}
          // After the select, whether to select the next target with the selected target (deselected if the target is selected again).
          continueSelect={false}
          // Determines which key to continue selecting the next target via keydown and keyup.
          toggleContinueSelect={"shift"}
          // The container for keydown and keyup events
          keyContainer={window}
          // The rate at which the target overlaps the drag area to be selected. (default: 100)
          hitRate={100}
          onSelect={(e) => {
            if (e.el) {
              if (isSelected) {
                const newArr = [...selectedPixels];
                const index = newArr.indexOf(`${i}_${idx}`);
                if (index > -1) {
                  newArr.splice(index, 1);
                }
                setSelected(newArr);
              } else {
                setSelected((state) => [...state, `${i}_${idx}`]);
              }
            }
          }}
        />
        <TransformComponent
          wrapperStyle={{
            maxWidth: "100%",
            maxHeight: "100vh",
          }}
        >
          <div className="container">
            {mapArray?.map((arr, i) => {
              return arr.map((el, idx) => {
                const isSelected = selectedPixels.find(
                  (x) => x === `${i}_${idx}`
                );
                return (
                  <div className="child">
                    <div
                      key={`${i}_${idx}`}
                      className={el ? (isSelected ? "selected" : "pix") : "ele"}
                      onClick={() => {
                        if (el) {
                          if (isSelected) {
                            const newArr = [...selectedPixels];
                            const index = newArr.indexOf(`${i}_${idx}`);
                            if (index > -1) {
                              newArr.splice(index, 1);
                            }
                            setSelected(newArr);
                          } else {
                            setSelected((state) => [...state, `${i}_${idx}`]);
                          }
                        }
                      }}
                    ></div>
                  </div>
                );
              });
            })}
          </div>
        </TransformComponent>
      </div>
    </TransformWrapper>
  );
}
